#| standalone: true
#| components: [editor, viewer]
shinyApp(
  ui = fluidPage(
    numericInput("num", "Enter number",
                 value = 1),
    textOutput("result")
  ),
  server = function(input, output) {
    val = input$num^2
    output$result &lt;- renderText({
      paste("Squared number is:", val)
    })
  }
)

